import React, { useState } from 'react';
import Common from './Common';
import image from "./images/slide-2.gif";


const Home = () =>{
    const texts =["Software Developer", "front-end Developer", "Quick Learner","......."];
    let count=0;
    let index = 0;
    let currentText="";
    let letter="";
    const [type, setType] = useState("") ;


(function type(){
    if (count === texts.length){
        count=0;
    }
    currentText = texts[count];
    letter = currentText.slice(0, ++index);
    if(letter.length === currentText.length){
        setType(letter);
        count++;
        index = 0;
    }
    setTimeout(type,250);
})();

    
    
    return <>
    
    <div 
    style = {{ backgroundImage: `url(${image})`, backgroundSize: "100% 100%", backgroundRepeat : "no-repeat", overflow : "hidden" }} 
    className="slide">

        <div 
        className="wrapper"
        >
            <h1>We make </h1>
            <h1 className="typing" id="type">{type}</h1>
        </div>

    {/* <div className="wrapper">
        <div className= "static-text">We make</div>
        <ul className="dynamic-texts">
            <li><span>Web App</span></li>
            <li><span>Websites</span></li>
            <li><span>Android App</span></li>
            <li><span>Data system</span></li>
        </ul>
        
    </div> */}
    </div>
    
      <Common 
      name='𝒢𝓇𝑜𝓌 𝓎𝑜𝓊𝓇 𝒷𝓊𝓈𝓈𝒾𝓃𝑒𝓈𝓈 𝓌𝒾𝓉𝒽'
      text='𝔐𝔜  ค𝔭𝔭𝔩𝔦𝔠𝔞𝔱𝔦𝔬𝔫 '
      visit='/service'
      btname='Get Started'/>
    </>
}

export default Home;
